        </div>
    </div>

    <!-- 批量导入提示 -->
    <div class="batch-import-tip flex-center" v-if="isBatchImportModalShow">
        <div class="batch-import-shade" @click="isBatchImportModalShow = false"></div>
        <div class="batch-tip-content" :style="`width: ${clientWidth - 60}px;`">
            <div class="tip-content-describe">批量导入功能请登录PC端使用, 域名:</div>

            <!-- 剪切板 -->
            <div class="tip-content-clipboard">
                <div class="tip-clipboard-content flex-start-center">
                    <div class="clipboard-content-left flex-rest">http://ycpd.hotgz.com/</div>
                    <div class="clipboard-content-right flex-center" id="clipboard-content-right" data-clipboard-text="http://ycpd.hotgz.com/">
                        <svg width="12" height="12" viewBox="0 0 26 26" version="1.1" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink"><g id="首页" stroke="none" stroke-width="1" fill="none" fill-rule="evenodd"><g id="增加客户-批量" transform="translate(-476.000000, -694.000000)" fill="#999999" fill-rule="nonzero"><g id="Alert" transform="translate(0.000000, 128.000000)"><g id="Group" transform="translate(95.000000, 420.000000)"><g id="Group-2" transform="translate(381.000000, 146.000000)">
                            <path d="M6,2 L6,0 L24,0 L24,22 L22,22 L22,2 L6,2 Z M2,4 L20,4 L20,26 L2,26 L2,4 Z M4,6 L4,24 L18,24 L18,6 L4,6 Z M6,10 L12,10 L12,12 L6,12 L6,10 Z M6,14 L16,14 L16,16 L6,16 L6,14 Z" id="icon_copy"></path></g></g></g></g></g>
                        </svg>
                        <button class="copy-invitation-code" id="copy-invitation-code" data-clipboard-text="rjhlwl" style="color: #2F8AFF;" @click="copyClipboard">复制验证码</button>
                    </div>
                </div>
            </div>

            <!-- 底部按钮 -->
            <div class="tip-content-bottom" @click="isBatchImportModalShow = false">确定</div>
        </div>
    </div>
</div>
</template>

<script>
// 框架类
import { Toast } from 'mint-ui';
// 请求类
import ajaxs from "@/api/customer/add";
import checkMaxAddNum from "@/api/common/checkMaxAddNum";
// 组件类
import initJSSDK from "@/components/initJSSDK";
import Consequencer from "@/utils/Consequencer";
import carNoInput from "@/components/carNoInput";
// 静态资源类
import register from "@/static/register.png";

export default {
    name: 'customer-add',

    components: { carNoInput },

	data: function data() { 
        return {
            clientWidth: document.body.offsetWidth || document.documentElement.clientWidth || window.innerWidth, // 设备的宽度
            clientHeight: document.body.offsetHeight || document.documentElement.clientHeight || window.innerHeight, // 设备高度

            picture: {
                register: register
            },

            /**
             * 选中的标签栏
             * @param {string} carNo 车牌号 默认
             * @param {string} vinNo 车架号
             */
            tagSelected: 'carNo',

            /**
             * 车牌
             */
            carNoComponents: { // 车牌组件来的数据
                verify: false,
                message: '',
                carNo: '粤',
                carNoProvince: '粤',
                plateNo: '',
                carType: '',
            },
            carNoProvince: '粤', // 车牌省份
            isProvincesKeyboardShow: false, // 是否显示 车牌省份
            provincesList: [ ["京", "沪", "浙", "苏", "粤", "鲁", "晋", "冀"],  ["豫", "川", "渝", "辽", "吉", "黑", "皖", "鄂"],  ["津", "贵", "云", "桂", "琼", "青", "新", "藏"],  ["蒙", "宁", "甘", "陕", "闽", "赣", "湘"] ], // 车牌省显示
            plateNo: '', // 车牌号码
            isPlateNoKeyboardShow: false, // 是否显示 车牌号码
            plateNoNumberlist: [1, 2, 3, 4, 5, 6, 7, 8, 9, 0], // 车牌数字
            plateNoCaptionList: ['Q', 'W', 'E', 'R', 'T', 'Y', 'U', 'I', 'O', 'A', 'S', 'D', 'F', 'G', 'H', 'J', 'K', 'P', 'Z', 'X', 'C', 'V', 'B', 'N', 'M', 'L'], // 车牌字母
            isNewEnergy: false, // 是否新能源汽车牌

            /**
             * 客户姓名手机号
             */
            customerName: '', 
            phoneNumber: '',

            /**
             * 拍照
             */
            photographStatus: 'carNo', // 选择图片状态 车牌号:carNo 车架号:vinNo
            isPhotographShow: false, // 是否显示 拍照模态框
            carNoPhotoSelected: '', // 车牌号 选择中的照片
            vinNoPhotoSelected: '', // 车架号 选择中的照片

            /**
             * 车架号
             */
            vinNo: '', // 车架号 
            engineNo: '', // 发动机号

            isBatchImportModalShow: false, // 是否显示 批量导入提示
        }
    },

    computed: {
        // 车牌省份的宽度
        intervalWidth: function intervalWidth() {
            var myIntervalWidth = (this.clientWidth - 288) / 18;
            myIntervalWidth = myIntervalWidth > 0 ? myIntervalWidth : 0;
            return myIntervalWidth;
        }
    },

    watch: {
        /**
         * 监听是否新能源
         */
        isNewEnergy: function isNewEnergy(newNewEnergy, oldNewEnergy) {
            /**
             * 新能源车牌 切换到 普通车牌的时候
             * 如果车牌号为 6位 则切换到 5位数
             */
            if (newNewEnergy === false && this.plateNo.length === 7) {
                this.plateNo = this.plateNo.slice(0, 6); // 裁剪掉多余
            }
        },
    },

	mounted: function mounted() {
        this.initAddCustomerToken(); // 获取添加客户的token
        this.checkMaxAddNum(); // 校验是否可以继续添加客户
        this.wxJSSDKchooseImage(); // 初始化拍照或从手机相册中选图接口
    },

	methods: {
        /**
         * 获取添加客户的token
         */
        initAddCustomerToken: function initAddCustomerToken() {
            const _this = this;

            ajaxs.getAddCustomerToken()
            .then(res => {
                // 缓存
                window.sessionStorage.setItem('saveClientToken', res.saveClientToken);
            }, error => alert(error));
        },

        /**
         * 校验是否可以继续添加客户
         */
        checkMaxAddNum: function checkMaxAddNum() {
            const _this = this;

            checkMaxAddNum(this)
            .then(
                res => {
                    if (res.code === 1008) {
                        alert('不好意思，您当前的客户数量名额已达到上限，不能再添加客户');
                        _this.jumpToRouter('/');
                    }
                }, error => alert(error)
            )
        },

        /**
         * 复制验证码
         */
        copyClipboard: function copyClipboard() {
            const _this = this;
            const input = document.createElement('input');
            document.body.appendChild(input);
            input.setAttribute('value', 'rjhlwl');
            input.contentEditable = true;
            input.readOnly = false;
            const range = document.createRange();
            range.selectNodeContents(input);
            const sel = window.getSelection();
            sel.removeAllRanges();
            sel.addRange(range);
            input.select();
            if (document.execCommand('copy')) {
                Toast({
                    message: '复制成功!',
                    duration: 1000
                });
                _this.isBatchImportModalShow = false;
            } else {
                // alert('复制失败，请手动复制！')
            }
            input.blur();
            input.setAttribute('style', 'visibility: hidden;');
        },

        /**
         * 从车牌输入的组件获取车牌号
         */
        carNoHandle: function carNoHandle(data) {
            this.carNoComponents = data;
        },

        /**
         * 初始化拍照或从手机相册中选图接口
         */
        wxJSSDKchooseImage: function wxJSSDKchooseImage() {
            initJSSDK(['chooseImage']).then(
                res => {
                    console.log('成功初始化拍照或从手机相册中选图接口');
                }, error => {
                    console.error('初始化拍照或从手机相册中选图失败');
                    console.error(error);
                }
            );
        },

        /**
         * 选择图片 通过 拍照 手机相册
         * @param {array} sourceType 指定来源是相册还是相机，默认二者都有
         */
        chooseImageBy: function chooseImageBy(sourceType) {
            const _this = this;
            let photographStatus = this.photographStatus; // 选择图片 车牌号:carNo 车架号:vinNo

            wx.chooseImage({
                count: 1, // 默认 选择一种图片
                sizeType: ['original', 'compressed'], // 可以指定是原图还是压缩图，默认二者都有
                sourceType: sourceType, // 可以指定来源是相册还是相机，默认二者都有
                success: function (res) {
                    console.log(res);
                    var localIds = res.localIds; // 返回选定照片的本地ID列表，localId可以作为img标签的src属性显示图片
                }
            });
        },

        /**
         * 校验车牌号码
         */
        verifyPlateNo: function verifyPlateNo() {
            if (this.carNoComponents.verify === true) {
                return Consequencer.success();
            } else {
                return Consequencer.error('车牌号有误.');
            }
        },

        /**
         * 校验用户名
         */
        verifyCustomerName: function verifyCustomerName() {
            // 判断车牌号码是否为空
            if (this.customerName === '') {
                return Consequencer.error('用户名不能为空');
            }
            
            return Consequencer.success();
        },

        /**
         * 校验手机号
         */
        verifyPhoneNumber: function verifyPhoneNumber() {
			if (this.phoneNumber === '') {
                return Consequencer.error('手机号码不能为空');
            }
            
            if (/^(?=\d{11}$)^1(?:3\d|4[57]|5[^4\D]|66|7[^249\D]|8\d|9[89])\d{8}$/.test(this.phoneNumber) === false) { // 正则匹配表达式: https://github.com/VincentSit/ChinaMobilePhoneNumberRegex
                return Consequencer.error('请输入正确的手机号码格式');
			}
            
            return Consequencer.success();
        },

        /**
         * 选择车牌省份
         */
        selectProvince: function selectProvince(item) {
            this.carNoProvince = item; // 设置车牌省份
            this.isProvincesKeyboardShow = false; // 隐藏遮罩层

            // 自动弹出输入车牌号
            if (this.verifyPlateNo().result !== 1) {
                this.isPlateNoKeyboardShow = true; // 隐藏遮罩层
            }
        },

        /**
         * 删除车牌号
         */
        deletePlateNo: function deletePlateNo() {
            if (this.plateNo.length > 0) {
                var cutLength = this.plateNo.length - 1;
                this.plateNo = this.plateNo.slice(0, cutLength);
            }
        },

        /**
         * 选择车牌号
         */
        selectPlateNo: function selectPlateNo(item) {
            // 判断是否新能源
            if (this.isNewEnergy) {
                // 大于6个的时候禁止改变(新能源)
                if (this.plateNo.length <= 6) {
                    this.plateNo += item;
                }
            } else {
                // 大于5个的时候禁止改变(非新能源)
                if (this.plateNo.length <= 5) {
                    this.plateNo += item;
                }
            }
        },

        /**
         * 通过车牌号添加客户
         */
        addCustomerByCarNo: function addCustomerByCarNo() {
            const _this = this;

            // 校验车牌号
            let verifyPlateNo = this.verifyPlateNo();
            if (verifyPlateNo.result !== 1) {
                return alert(verifyPlateNo.message);
            }
            
            // // 校验用户名 （非必填）
            // let verifyCustomerName = this.verifyCustomerName();
            // if (verifyCustomerName.result !== 1) {
            //     return alert(verifyCustomerName.message);
            // }

            // // 校验手机号 （非必填）
            // let verifyPhoneNumber = this.verifyPhoneNumber();
            // if (verifyPhoneNumber.result !== 1) {
            //     return alert(verifyPhoneNumber.message);
            // }

            ajaxs.addCustomerByCarNo(this.carNoComponents.carNo, this.customerName, this.phoneNumber)
            .then(
                res => {
                    // 添加成功
                    // 跳转到客户列表页
                    _this.$router.replace({path: `/customer`});

                }, error => alert(error)
            );
        },

        /**
         * 通过发动机号，车架号添加客户
         */
        addCustomerByVinNo: function addCustomerByVinNo() {
            const _this = this;

            // 校验车架号
			if (this.vinNo ===  '') {
                return alert('车架号不能为空');
            } else if (this.vinNo.length !==  17) { // 十七个英数组成, 英文字母“I”、“O”、“Q”均不会被使用
                return alert('请输入正确的车架号');
            }

            // 校验发动机号
			if (this.engineNo ===  '') {
                return alert('发动机号不能为空');
            } 
            // else if (this.engineNo.length < 6 || this.engineNo.length > 7 ) { // 6~7位
            //     return alert('请输入正确的发动机号');
            // }

            // // 校验用户名 （非必填）
            // let verifyCustomerName = this.verifyCustomerName();
            // if (verifyCustomerName.result !== 1) {
            //     return alert(verifyCustomerName.message);
            // }

            // // 校验手机号 （非必填）
            // let verifyPhoneNumber = this.verifyPhoneNumber();
            // if (verifyPhoneNumber.result !== 1) {
            //     return alert(verifyPhoneNumber.message);
            // }

            ajaxs.addCustomerByVinNo(this.vinNo, this.engineNo, this.customerName, this.phoneNumber, this)
            .then(
                res => { // 添加成功
                    // 跳转到客户列表页
                    _this.$router.replace({path: `/customer`});

                }, error => alert(error)
            );
        },

        /**
         * 弹出模态框的时候 需要调用一下滚动位置，因为有些手机的屏幕不行
         */
        scrollToShowKeyboard: function scrollToShowKeyboard() {
            window.scrollTo(0, 150);
        },

        /**
         * 跳转到路由
         * @param {object} query 携带的参数 非必填
         */
        jumpToRouter: function jumpToRouter(url, query) {
            let routerConfig = {
                path: url,
            }

            query ? routerConfig.query = query : null; // 初始化携带的参数 非必填

            this.$router.push(routerConfig);
        },
    }
}

</script>

