<script>

// 框架类
import Vue from "vue";
// 请求类
import ajaxsgetAllActivity from "@/api/common/getAllActivity";
// 配置类
import config from "@/config/index.js";
// 组件类
import Tabbar from "@/components/Tabbar";
import html5WxBMapLocation from "@/components/html5WxBMapLocation";
import { Toast, Swipe, SwipeItem } from 'mint-ui';
// 初始化类
Vue.component('mt-swipe', Swipe);
Vue.component('mt-swipe-item', SwipeItem);

export default {
    name: 'home',

    components: { Tabbar },

	data: function data() { 
        return {
            clientWidth: document.body.offsetWidth || document.documentElement.clientWidth || window.innerWidth, // 设备的宽度
            clientHeight: document.body.offsetHeight || document.documentElement.clientHeight || window.innerHeight, // 设备高度

            news: [ // 新闻
                {
                    picture: 'https://ycpd-assets.oss-cn-shenzhen.aliyuncs.com/pingan-wechatapplets/home/banner/001image001.jpg?x-oss-process=image/resize,m_fill,w_75,h_55,limit_0/auto-orient,0/quality,q_100',
                    title: '新闻公告标题新闻公告标题新闻公告标题新闻公告标题',
                    time: '2018-10-6 16:43:01',
                }, {
                    picture: 'https://ycpd-assets.oss-cn-shenzhen.aliyuncs.com/pingan-wechatapplets/home/banner/001image002.jpg?x-oss-process=image/resize,m_fill,w_75,h_55,limit_0/auto-orient,0/quality,q_100',
                    title: '新闻公告标题新闻公告标题新闻公告标题新闻公告标题',
                    time: '2018-10-6 16:43:01',
                }
            ],

            projectId_1: '', // 活动1的id
            projectId_2: '', // 活动2的id
        } 
    },
    
    computed: {
        /**
         * 从 store 获取数据 用户信息
         */
        userInfoStore: function userInfoStore() {
            let ycpd_userInfo = window.localStorage.getItem('ycpd_userInfo');

            return ycpd_userInfo ? JSON.parse(ycpd_userInfo) : this.$store.getters["userInfo/getAgentInfo"]; // 因为数据刷新页面会失效, 所以优先使用 window.localStorage
        },

        /**
         * 用于判断是否是一级代理
         * parentId === 1 或者为空 就是一级代理
         * 如果 不等于 1 或者为空 或者就二级代理
         */
        isPXV: function isPXV() {
            // 判断存在不存在 parentId
            if (this.userInfoStore.parentId) {

                // 如果存在parentId，则判断是不是 === 1
                if (parseInt(this.userInfoStore.parentId) === 1) {
                    // 肯定是一级代理
                    return true;
                } else {
                    return false;
                }
            } else {
                // 如果不存在 parentId， 则有可能是一级代理
                return true;
            }
        },
    },

	mounted: function mounted() {
        this.getActivity(); // 获取 - 所有活动列表
    },

	methods: {
        /**
         * 获取 - 所有活动列表
         */
        getActivity: function getActivity() {
            const _this = this;

            ajaxsgetAllActivity(this)
            .then(
                res => {
                    if (res && res[0] && res[0].projectId) {
                        _this.projectId_1 = res[0].projectId;
                    }

                    if (res && res[1] && res[1].projectId) {
                        _this.projectId_2 = res[1].projectId;
                    }

                }, error => {
                    alert(error);
                }
            );
        },

        /**
         * 跳转到 优惠加油
         */ 
        jumpToGasStation: function jumpToGasStation() {
            window.location.href = config.jumpwx(`${config.location.ycpd}#/redirect/gasstation`, config.wxappid.ycpd);
        },

        /**
         * 跳转到 领取停车券
         */
        jumpToGetParkTik: function jumpToGetParkTik() {
            window.location.href = 'https://rec.mobi88.cn/index.php/Theme/index.html?mark=10001&mobile=18927403415&code=021hvtSh1fq5vx0qQKTh1KHCSh1hvtSp&state=1';
        },

        /**
         * 升级中
         */
        upgrading: function upgrading() {
            Toast({ message: "升级中", duration: 1000 });
        },

        /**
         * 跳转到路由
         * @param {object} query 携带的参数 非必填
         */
        jumpToRouter: function jumpToRouter(url, query) {
            let routerConfig = {
                path: url,
            }

            query ? routerConfig.query = query : null; // 初始化携带的参数 非必填

            this.$router.push(routerConfig);
        },
    }
}

</script>

